mapper.select:
SELECT sui.id, sui.skill_id, sui.skill_name, sui.skill_level, sui.skiller_id, CONCAT('(',sui.skiller_id,')',c.user_name) skillerInfo, sui.STATUS, sui.remark, sui.is_deleted, sui.create_user, CONCAT('(',sui.create_user,')',d.user_name) createUserName, sui.create_date, sui.update_user, CONCAT('(',sui.update_user,')',e.user_name) updateUserName, sui.update_date FROM skill_user_info sui LEFT JOIN skiller_info si ON sui.skiller_id = si.id LEFT JOIN sys_user c ON si.user_id = c.user_id LEFT JOIN sys_user d ON sui.create_user = d.user_id LEFT JOIN sys_user e ON sui.update_user = e.user_id
sui.is_deleted = 0 and sui.skill_id = #{skillId}
and (sui.skill_id LIKE CONCAT('%',#{skillName},'%') OR sui.skill_name LIKE CONCAT('%',#{skillName},'%'))
and sui.skill_level = #{skillLevel} and sui.skiller_id = #{skillerId}
and (sui.skiller_id LIKE CONCAT('%',#{skillerInfo},'%') OR c.user_name LIKE CONCAT('%',#{skillerInfo},'%'))
and sui.status = #{status} and sui.is_deleted = #{isDeleted} and sui.create_user like concat('%', #{createUser}, '%') and sui.create_date between #{params.beginCreateDate} and #{params.endCreateDate} and sui.update_user like concat('%', #{updateUser}, '%') and sui.update_date between #{params.beginUpdateDate} and #{params.endUpdateDate}

SELECT
    COUNT(sui.id)
FROM
    skill_user_info sui
    LEFT JOIN sys_user d ON sui.create_user = d.user_id
    LEFT JOIN sys_user e ON sui.update_user = e.user_id

    sui.is_deleted = 0
     and sui.skill_id = #{skillId}
     and sui.skill_name like concat('%', #{skillName}, '%')
     and sui.skill_level = #{skillLevel}
     and sui.skiller_id = #{skillerId}
     and sui.status = #{status}
     and sui.is_deleted = #{isDeleted}
     and sui.create_user like concat('%', #{createUser}, '%')
     and sui.create_date between #{params.beginCreateDate} and #{params.endCreateDate}
     and sui.update_user like concat('%', #{updateUser}, '%')
     and sui.update_date between #{params.beginUpdateDate} and #{params.endUpdateDate}

mapper.update:
update skill_user_info
skill_id = #{skillId}, skill_name = #{skillName}, skill_level = #{skillLevel}, skiller_id = #{skillerId}, status = #{status}, remark = #{remark}, is_deleted = #{isDeleted}, create_user = #{createUser}, create_date = #{createDate}, update_user = #{updateUser}, update_date = #{updateDate},
where id = #{id}
