mapper.select:
SELECT a.id, a.user_id, CONCAT('(',a.user_id,')',b.user_name) userInfo, a.power, a.evaluation, a.address_id, CONCAT('(',a.address_id,')',c.`name`) addressInfo, a.STATUS, a.remark, a.is_deleted, a.create_user, CONCAT('(',a.create_user,')',d.user_name) createUserName, a.create_date, a.update_user, CONCAT('(',a.update_user,')',e.user_name) updateUserName, a.update_date FROM skiller_info a LEFT JOIN sys_user b ON a.user_id = b.user_id LEFT JOIN address_info c ON a.address_id = c.id LEFT JOIN sys_user d ON a.create_user = d.user_id LEFT JOIN sys_user e ON a.update_user = e.user_id
and a.user_id = #{userId}
and (b.user_name LIKE CONCAT('%',#{userInfo},'%') OR a.user_id LIKE CONCAT('%',#{userInfo},'%') )
and a.power = #{power} and a.evaluation = #{evaluation} and a.address_id = #{addressId}
and (c.name LIKE CONCAT('%',#{addressInfo},'%') OR a.address_id LIKE CONCAT('%',#{addressInfo},'%') )
and a.status = #{status} and a.is_deleted = #{isDeleted} and a.create_user like concat('%', #{createUser}, '%') and a.create_date between #{params.beginCreateDate} and #{params.endCreateDate} and a.update_user like concat('%', #{updateUser}, '%') and a.update_date between #{params.beginUpdateDate} and #{params.endUpdateDate}

mapper.update:
update skiller_info
user_id = #{userId}, power = #{power}, evaluation = #{evaluation}, address_id = #{addressId}, status = #{status}, remark = #{remark}, is_deleted = #{isDeleted}, create_user = #{createUser}, create_date = #{createDate}, update_user = #{updateUser}, update_date = #{updateDate},
where id = #{id}
